    })
    return (
        <View  style={styles.searchContainer}>
           <TextInput placeholder="Search...." style={styles.searchInput}/>
        </View>
    )

}
const styles =StyleSheet.create({
   searchContainer:{
       backgroundColor:Colors.secondary,
   },
   searchInput:{
       paddingHorizontal:20,
       paddingVertical:10,
       margin:20,
