import React,{useState,useEffect} from 'react';
import {
    View,
    Text,
    FlatList,
    TouchableOpacity,
    StyleSheet,
    Image
} from 'react-native';
import Colors from '../utils/colors';
import * as SQLite from "expo-sqlite";
import { Feather } from "@expo/vector-icons";
// const db = SQLite.openDatabase('productRate.db')

export default function product({name,price,MOQ,date,img,detail,location}){
    return (
      <View style={styles.container}>
          <Image style={styles.image}  source={img} />
         <View style={styles.itmeInfo}>
           <View style={styles.detail}>
                <Text style={styles.productName}>{name}</Text>
                <Text style={styles.productPrice}>{price} AFG</Text>
                <Text style={styles.moq}>MOQ : {MOQ}</Text>
                <Text style={styles.date}>Sine {date}</Text>
                <Text style={styles.date}>Location {location}</Text>
            </View>
           
        </View>
        <View>
                <Text>
                    Material Details{detail}
                </Text>
            </View>
      </View>
     
    )

}


const styles =StyleSheet.create({
    container:{
    
     
    },
    itmeInfo:{
      flexDirection:'row',
      paddingVertical:16,
      paddingHorizontal:10,
      boxShadow:'3px 3px 8px 0 rgba(0, 0, 0, 0.1), 3px 3px 8px 0 rgba(0, 0, 0, 0.1)',
      marginVertical:5,
      justifyContent:'center'
    },
    productName :{
      fontWeight:'bold',
      fontSize:20
    },
    productPrice :{
      fontWeight:'bold',
      fontSize:20
    },
    moq:{
      color:'gray',
      fontSize:16
    },
    date:{
      color:'gray',
      fontSize:16
    },
    image:{
        width:150,
        marginRight:20
    }

   
})
